import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loginSuccess, loginFailed } from "./redux/actions/auth.actions";
import { Link, useNavigate } from "react-router-dom"; // Importe useNavigate pour la redirection
import logo from "./img/argentBankLogo.png";

const SignInForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();  // Utilise useNavigate pour rediriger après la connexion
  const { error } = useSelector((state) => state.auth);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form submitted');

    try {
      const response = await fetch('http://localhost:3001/api/v1/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: username, password }),
      });

      const data = await response.json();
      console.log('API response:', data);

      if (response.ok) {
        dispatch(loginSuccess(data.body.token));  // Dispatch l'action de succès avec le token
        // Redirection vers la page utilisateur après connexion réussie
        navigate('/user');  // Redirige vers la page de l'utilisateur après connexion
      } else {
        dispatch(loginFailed(data.message));  // Dispatch l'action d'échec
      }
    } catch (error) {
      dispatch(loginFailed("Network error"));
    }
  };

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="input-remember">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button type="submit" className="sign-in-button">Sign In</button>
          {error && <p className="error">{error}</p>}  {/* Affiche les erreurs */}
        </form>
      </section>
    </main>
  );
};


const Footer = () => {
  return (
    <footer className="footer">
      <p className="footer-text">Copyright 2020 Argent Bank</p>
    </footer>
  );
};

const SignIn = () => {
  return (
    <>
      <Nav />
      <SignInForm />
      <Footer />
    </>
  );
};

export default SignIn;
